mapper.insert:

    SELECT SEQ_BIZ_CODE.NEXTVAL FROM DUAL

INSERT INTO TB_BIZ_CODE (
BIZ_SEQ
, PARENT_SEQ
, LEVEL_NO
, NAME
, DESCRIPTION
, DEL_YN
, REG_ID
, REG_DT
) VALUES (
#{bizSeq,       jdbcType=INTEGER}
, #{parentSeq,    jdbcType=INTEGER}
, #{levelNo,      jdbcType=INTEGER}
, #{name,         jdbcType=VARCHAR}
, #{description,  jdbcType=VARCHAR}
, 'N'
, #{regId,        jdbcType=VARCHAR}
, SYSDATE
)

mapper.update:
UPDATE TB_BIZ_CODE
SET MOD_DT = SYSDATE
, NAME = #{name}
, DESCRIPTION = #{description}
, MOD_ID = #{modId}
WHERE BIZ_SEQ = #{bizSeq}
