mapper.select:
SELECT REQ_SEQ , BIZ_SEQ , REQ_ID , REQ_NAME , REQ_DESC , REQ_TYPE_CODE , PRIORITY_CODE , STATUS_CODE , PARENT_SEQ , OWNER_ID , PLAN_START_DT , PLAN_END_DT , ACT_START_DT , ACT_END_DT , ORDER_NO , DEL_YN , REG_ID , REG_DT , MOD_ID , MOD_DT FROM TB_REQUIREMENT WHERE DEL_YN = 'N'
AND BIZ_SEQ = #{bizSeq}
ORDER BY PARENT_SEQ NULLS FIRST, ORDER_NO NULLS LAST, REQ_SEQ

mapper.insert:

    SELECT nextval('seq_requirement')

INSERT INTO TB_REQUIREMENT (
REQ_SEQ
, BIZ_SEQ
, REQ_ID
, REQ_NAME
, REQ_DESC
, REQ_TYPE_CODE
, PRIORITY_CODE
, STATUS_CODE
, PARENT_SEQ
, OWNER_ID
, PLAN_START_DT
, PLAN_END_DT
, ACT_START_DT
, ACT_END_DT
, ORDER_NO
, DEL_YN
, REG_ID
, REG_DT
) VALUES (
#{reqSeq, jdbcType=BIGINT}
, #{bizSeq, jdbcType=BIGINT}
, 'REQ-' || 'RMS' || '-' || LPAD(fn_next_seq('REQ', 'RMS', '-')::text, 4, '0')
, #{reqName, jdbcType=VARCHAR}
, #{reqDesc, jdbcType=VARCHAR}
, #{reqTypeCode, jdbcType=VARCHAR}
, #{priorityCode, jdbcType=VARCHAR}
, #{statusCode, jdbcType=VARCHAR}
, #{parentSeq, jdbcType=BIGINT}
, #{ownerId, jdbcType=VARCHAR}
, #{planStartDt, jdbcType=TIMESTAMP}
, #{planEndDt, jdbcType=TIMESTAMP}
, #{actStartDt, jdbcType=TIMESTAMP}
, #{actEndDt, jdbcType=TIMESTAMP}
, #{orderNo, jdbcType=INTEGER}
, 'N'
, #{regId, jdbcType=VARCHAR}
, CURRENT_TIMESTAMP
)

mapper.update:
UPDATE TB_REQUIREMENT
SET MOD_DT = CURRENT_TIMESTAMP
        , REQ_NAME = #{reqName} 
        , REQ_DESC = #{reqDesc} 
    , REQ_TYPE_CODE = #{reqTypeCode} 
   , PRIORITY_CODE = #{priorityCode} 
     , STATUS_CODE = #{statusCode} 
        , OWNER_ID = #{ownerId} 
    , PLAN_START_DT = #{planStartDt} 
      , PLAN_END_DT = #{planEndDt} 
     , ACT_START_DT = #{actStartDt} 
       , ACT_END_DT = #{actEndDt} 
        , ORDER_NO = #{orderNo} 
          , MOD_ID = #{modId} 
         , BIZ_SEQ = #{bizSeq} 
WHERE REQ_SEQ = #{reqSeq}
AND DEL_YN = 'N'

UPDATE TB_REQUIREMENT
SET ORDER_NO = #{orderNo}
, MOD_ID = #{modId}
, MOD_DT = CURRENT_TIMESTAMP
WHERE REQ_SEQ = #{reqSeq}
