mapper.insert:

    SELECT SEQ_COMMON_CODE.NEXTVAL FROM DUAL

INSERT INTO TB_COMMON_CODE
(
CODE_SEQ
, CODE_GROUP
, CODE_ID
, CODE_NAME
, PARENT_SEQ
, SORT_ORDER
, DEL_YN
, REG_ID
, REG_DT
)
VALUES
(
#{codeSeq,    jdbcType=NUMERIC}
, #{codeGroup,  jdbcType=VARCHAR}
, #{codeId,     jdbcType=VARCHAR}
, #{codeName,   jdbcType=VARCHAR}
, #{parentSeq,  jdbcType=NUMERIC}
, #{sortOrder,  jdbcType=NUMERIC}
, 'N'
, #{regId,      jdbcType=VARCHAR}
, SYSDATE
)

mapper.update:
UPDATE TB_COMMON_CODE
SET   MOD_DT = SYSDATE
 , CODE_GROUP = #{codeGroup, jdbcType=VARCHAR} 
    , CODE_ID    = #{codeId,    jdbcType=VARCHAR} 
  , CODE_NAME  = #{codeName,  jdbcType=VARCHAR} 
 , PARENT_SEQ = #{parentSeq, jdbcType=NUMERIC} 
 , SORT_ORDER = #{sortOrder, jdbcType=NUMERIC} 
     , MOD_ID     = #{modId,     jdbcType=VARCHAR} 
WHERE CODE_SEQ = #{codeSeq, jdbcType=NUMERIC}
AND DEL_YN = 'N'

UPDATE TB_COMMON_CODE
SET   SORT_ORDER = #{sortOrder, jdbcType=NUMERIC}
  , MOD_ID     = #{modId,     jdbcType=VARCHAR}
  , MOD_DT     = SYSDATE
WHERE CODE_SEQ   = #{codeSeq,   jdbcType=NUMERIC}
